mapper.select:
SELECT T2.* FROM ( SELECT @r AS _id, ( SELECT @r := parent FROM `tf_area` WHERE id = _id ) AS 2v2, @l := @l + 1 AS lvl , vars.parent FROM ( SELECT @r := #{id} AS parent, @l := 0 ) vars, tf_area h
) T1 LEFT JOIN tf_area T2 ON T1._id = T2.id
WHERE T2.parent IS NULL AND T2.id IS NOT NULL

mapper.insert:
insert into tf_area (id, create_date, modify_date, 
  orders, name, tree_path, 
  parent, is_deleted, full_name
  )
values (#{id,jdbcType=BIGINT}, #{createDate,jdbcType=TIMESTAMP}, #{modifyDate,jdbcType=TIMESTAMP}, 
  #{orders,jdbcType=INTEGER}, #{name,jdbcType=VARCHAR}, #{treePath,jdbcType=VARCHAR}, 
  #{parent,jdbcType=BIGINT}, #{isDeleted,jdbcType=BIT}, #{fullName,jdbcType=LONGVARCHAR}
  )
