mapper.select:
select
count(id)
from tf_journalism
where 1 = 1

  AND company_id = #{companyId,jdbcType=BIGINT}


  and status = #{status,jdbcType=BIGINT}

select
count(id)
from tf_journalism
where 1 = 1

  AND company_id = #{companyId,jdbcType=BIGINT}


  and status = #{status,jdbcType=BIGINT}

SELECT '新闻动态' AS name ,COUNT(id) AS countNum, IFNULL(SUM(hits),0) AS sumHits  FROM tf_journalism WHERE 1 = 1

  and company_id = #{companyId,jdbcType=BIGINT}


  and create_date BETWEEN #{startDate,jdbcType=TIMESTAMP}  AND #{endDate,jdbcType=TIMESTAMP}

UNION ALL
SELECT '学术活动' AS name ,COUNT(id) AS countNum, IFNULL(SUM(hits),0) AS sumHits  FROM tf_learning WHERE 1 = 1

  and company_id = #{companyId,jdbcType=BIGINT}


  and create_date BETWEEN #{startDate,jdbcType=TIMESTAMP}  AND #{endDate,jdbcType=TIMESTAMP}

UNION ALL
SELECT '媒体报道' AS name ,COUNT(id) AS countNum , IFNULL(SUM(hits),0) AS sumHits FROM tf_media WHERE 1 = 1

  and company_id = #{companyId,jdbcType=BIGINT}


  and create_date BETWEEN #{startDate,jdbcType=TIMESTAMP}  AND #{endDate,jdbcType=TIMESTAMP}

UNION ALL
SELECT '会议展览' AS name ,COUNT(id) AS countNum , IFNULL(SUM(hits),0) AS sumHits  FROM tf_media WHERE  1 = 1

  and company_id = #{companyId,jdbcType=BIGINT}


  and create_date BETWEEN #{startDate,jdbcType=TIMESTAMP}  AND #{endDate,jdbcType=TIMESTAMP}

SELECT '微官网' AS NAME ,COUNT(1) AS countNum  FROM `tf_company_statistics` WHERE 1 = 1

  and company_id = #{companyId,jdbcType=BIGINT}

UNION ALL
SELECT '产品' AS NAME ,COUNT(1) AS countNum FROM `tf_product_statistics` WHERE 1 = 1

  and company_id = #{companyId,jdbcType=BIGINT}

UNION ALL
SELECT '文章' AS NAME, SUM(A.sumHits) AS countNum FROM (
SELECT  IFNULL(SUM(hits),0) AS sumHits FROM tf_journalism WHERE 1 = 1

  and company_id = #{companyId,jdbcType=BIGINT}

UNION ALL
SELECT IFNULL(SUM(hits),0) AS sumHits FROM tf_learning WHERE 1 = 1

  and company_id = #{companyId,jdbcType=BIGINT}

UNION ALL
SELECT IFNULL(SUM(hits),0) AS sumHits FROM tf_media WHERE 1 = 1

  and company_id = #{companyId,jdbcType=BIGINT}

UNION ALL
SELECT IFNULL(SUM(hits),0) AS sumHits FROM tf_exhibition WHERE 1 = 1

  and company_id = #{companyId,jdbcType=BIGINT}

) A

SELECT * FROM ( SELECT A.*,C.sumHits , CONCAT(ROUND(A.hits/C.sumHits*100,1),'%') AS 'proportion' FROM ( SELECT title,'新闻动态' AS 'articleType', hits ,create_date ,company_id FROM tf_journalism UNION ALL SELECT title,'学术活动' AS 'articleType', hits ,create_date ,company_id FROM tf_learning UNION ALL SELECT title,'媒体报道' AS 'articleType', hits ,create_date ,company_id FROM tf_media UNION ALL SELECT title,'会议展览' AS 'articleType', hits ,create_date ,company_id FROM tf_exhibition ) A , ( SELECT SUM(B.hits) AS sumHits FROM ( SELECT title,'新闻动态' AS 'articleType', hits ,create_date ,company_id FROM tf_journalism UNION ALL SELECT title,'学术活动' AS 'articleType', hits ,create_date ,company_id FROM tf_learning UNION ALL SELECT title,'媒体报道' AS 'articleType', hits ,create_date ,company_id FROM tf_media UNION ALL SELECT title,'会议展览' AS 'articleType', hits ,create_date ,company_id FROM tf_exhibition ) B)C ORDER BY A.hits DESC ) D WHERE 1 = 1
and company_id = #{companyId,jdbcType=BIGINT}
and create_date BETWEEN #{startDate,jdbcType=TIMESTAMP} AND #{endDate,jdbcType=TIMESTAMP}

SELECT j.*,c.`name` AS cname FROM tf_journalism j LEFT JOIN tf_company c ON j.`company_id` = c.`id`
where 1 = 1

  and j.title like '%${title}%'


  and j.create_date BETWEEN #{startDate,jdbcType=TIMESTAMP}  AND #{endDate,jdbcType=TIMESTAMP}


  and j.keyWord  like '%${keyWord}%'


  and j.datasource = #{datasource,jdbcType=VARCHAR}


  and j.company_id = #{companyId,jdbcType=BIGINT}


  and  j.content like '%${content}%'


  and j.status = #{status,jdbcType=BIGINT}


  AND  TO_DAYS( NOW( ) ) - TO_DAYS(j.create_date) <= 1


  AND DATE_SUB(CURDATE(), INTERVAL 7 DAY) <= DATE(j.create_date)


  AND PERIOD_DIFF( date_format( now( ) , '%Y%m' ) , date_format(j.create_date, '%Y%m' ) ) = 1


  AND year(j.create_date)=year(date_sub(now(),interval 1 year))


  and j.create_date BETWEEN #{startDate,jdbcType=TIMESTAMP}  AND #{endDate,jdbcType=TIMESTAMP}

ORDER BY j.create_date DESC
