mapper.select:
     SELECT COALESCE (B.countNum,0) countNum FROM (
    SELECT DATE_FORMAT(create_date, '%Y') YEAR  FROM tf_member GROUP BY YEAR
	      ) A LEFT JOIN (SELECT DATE_FORMAT(create_date, '%Y') YEAR,system,COUNT(id) countNum
	FROM
	    tf_member WHERE  system = #{system}
	GROUP BY
	      DATE_FORMAT(create_date, '%Y') ,system  ORDER BY YEAR
	    ) B ON A.year = B.year ORDER BY A.year

     SELECT COALESCE (B.countNum,0) countNum FROM (
    SELECT DATE_FORMAT(create_date, '%Y') YEAR  FROM tf_member GROUP BY YEAR
	      ) A LEFT JOIN (SELECT DATE_FORMAT(create_date, '%Y') YEAR,province,COUNT(id) countNum
	FROM
	    tf_member WHERE  province = #{province}
	GROUP BY
	      DATE_FORMAT(create_date, '%Y') ,province  ORDER BY YEAR
	    ) B ON A.year = B.year ORDER BY A.year

     SELECT COALESCE (B.countNum,0) countNum FROM (
    SELECT DATE_FORMAT(create_date, '%Y') YEAR  FROM tf_member GROUP BY YEAR
	      ) A LEFT JOIN (SELECT DATE_FORMAT(create_date, '%Y') YEAR,source,COUNT(id) countNum
	FROM
	    tf_member WHERE  source = #{source}
	GROUP BY
	      DATE_FORMAT(create_date, '%Y') ,source  ORDER BY YEAR
	    ) B ON A.year = B.year ORDER BY A.year

SELECT COUNT(id) FROM tf_member
WHERE 1 = 1

  AND PERIOD_DIFF( DATE_FORMAT( NOW( ) , '%Y%m' ) , DATE_FORMAT( create_date, '%Y%m' ) ) =#{month,jdbcType=BIGINT}

SELECT COUNT(id) FROM tf_member
WHERE 1 = 1

  AND TO_DAYS(NOW()) - TO_DAYS(create_date) = #{day,jdbcType=BIGINT}

    SELECT M.`source`,SUM(A.countMember) AS countMemberSource  FROM  (
    SELECT member,COUNT(1) AS countMember FROM `tf_product_statistics`
    WHERE  1 = 1
    
      AND company_id = #{companyId}
    
    
      and create_date BETWEEN #{startDate,jdbcType=TIMESTAMP}  AND #{endDate,jdbcType=TIMESTAMP}
    
    GROUP BY member
    ) A LEFT JOIN tf_member M  ON M.id = A.member GROUP BY source
