mapper.select:
SELECT
COUNT(id)
FROM tf_ad
WHERE 1 = 1

  AND company_id = #{companyId,jdbcType=BIGINT}


  AND status = #{status,jdbcType=BIGINT}

SELECT COUNT(id) FROM tf_ad WHERE NOW() >= begin_date AND NOW() <= end_date
AND company_id = #{companyId,jdbcType=BIGINT}
AND status = #{status,jdbcType=BIGINT}

SELECT COUNT(id) FROM tf_ad WHERE NOW() >= end_date
AND company_id = #{companyId,jdbcType=BIGINT}
AND status = #{status,jdbcType=BIGINT}

SELECT ad.*, ( CASE
WHEN ad.begin_date > NOW() AND ad.STATUS = 2 THEN '待展示' WHEN ad.begin_date < NOW() AND ad.end_date > NOW() AND ad.STATUS =2 THEN '在展示' WHEN ad.end_date < NOW() AND ad.STATUS =2 THEN '已过期' ELSE '-'
WHEN ad.begin_date > NOW() AND ad.STATUS = 2 THEN '待展示'
WHEN ad.begin_date < NOW() AND ad.end_date > NOW() AND ad.STATUS = 2 THEN '在展示'
WHEN ad.end_date < NOW() AND ad.STATUS = 2 THEN '已过期'
END ) AS show_status, ( CASE WHEN ad.begin_date < NOW() AND ad.end_date > NOW() AND ad.STATUS =3 AND ((TO_DAYS(ad.end_date) - TO_DAYS(NOW())) < 30 ) THEN TRUE ELSE FALSE END ) AS is_verdue, adp.name AS adName ,COALESCE(com.name,'自营广告') AS companyName FROM tf_ad ad LEFT JOIN tf_ad_position AS adp ON ad.ad_position = adp.id LEFT JOIN tf_company AS com ON ad.company_id = com.id where 1 = 1
AND ad.company_id is null
AND ad.company_id = #{companyId,jdbcType=BIGINT}
AND ad.title LIKE '%${title}%'
AND ad.ad_position = #{adPosition,jdbcType=BIGINT}
and ad.create_date BETWEEN #{startDate,jdbcType=TIMESTAMP} AND #{endDate,jdbcType=TIMESTAMP}
AND ad.status = #{status,jdbcType=BIGINT}
