mapper.select:
SELECT DATE_FORMAT(create_date,'%Y/%m') AS TIME,COUNT(1) AS satistics FROM tf_demonstration_statistics
WHERE 1 = 1

  AND company_id = #{companyId,jdbcType=BIGINT}


  AND type = #{type,jdbcType=BIGINT}

 GROUP BY TIME ORDER BY TIME  DESC LIMIT 12

SELECT DATE_FORMAT(create_date, '%Y') year , COUNT(id) AS countNum,type FROM tf_demonstration_statistics WHERE 1 = 1
AND company_id = #{companyId,jdbcType=BIGINT}
AND type = #{dtype,jdbcType=BIGINT}
GROUP BY DATE_FORMAT(create_date, '%Y') ORDER BY year DESC

SELECT COALESCE( B.countNum,0) countNum FROM ( SELECT CONCAT(#{year} ,'-01') AS mon UNION SELECT CONCAT(#{year} ,'-02') AS mon UNION SELECT CONCAT(#{year} ,'-03') AS mon UNION SELECT CONCAT(#{year},'-04') AS mon UNION SELECT CONCAT(#{year} ,'-05') AS mon UNION SELECT CONCAT(#{year} ,'-06') AS mon UNION SELECT CONCAT(#{year} ,'-07') AS mon UNION SELECT CONCAT(#{year} ,'-08') AS mon UNION SELECT CONCAT(#{year} ,'-09') AS mon UNION SELECT CONCAT(#{year} ,'-10') AS mon UNION SELECT CONCAT(#{year} ,'-11') AS mon UNION SELECT CONCAT(#{year} ,'-12') AS mon ) A LEFT JOIN ( SELECT DATE_FORMAT(create_date, '%Y-%m') createTime, COUNT(id) countNum FROM tf_demonstration_statistics WHERE 1 = 1
AND company_id = #{companyId,jdbcType=BIGINT}
AND type = #{type,jdbcType=BIGINT}
GROUP BY DATE_FORMAT(create_date, '%Y-%m') ) B ON A.mon = B.createTime order by A.mon

     SELECT COALESCE (D.countNum,0) countNum FROM (
    SELECT DATE_FORMAT(create_date, '%Y') YEAR  FROM tf_demonstration_statistics where type = #{type} GROUP BY YEAR
	      ) C LEFT JOIN (SELECT DATE_FORMAT(A.create_date, '%Y') YEAR,B.name,COUNT(A.id) countNum
	FROM
	    tf_demonstration_statistics A,tf_product B WHERE A.product = B.id and A.type = #{type} and B.name = #{name}
	GROUP BY DATE_FORMAT(A.create_date, '%Y') ,B.name  ORDER BY YEAR
	    ) D ON C.year = D.year ORDER BY  C.year DESC

                 SELECT COALESCE (B.countNum,0) countNum FROM (
    SELECT DATE_FORMAT(create_date, '%Y') YEAR  FROM tf_demonstration_statistics where  type = #{type} GROUP BY YEAR
	      ) A LEFT JOIN (SELECT DATE_FORMAT(create_date, '%Y') YEAR,province,COUNT(id) countNum
	FROM
	    tf_demonstration_statistics WHERE  type = #{type} and province = #{name}
	GROUP BY
	      DATE_FORMAT(create_date, '%Y') ,province  ORDER BY YEAR
	    ) B ON A.year = B.year ORDER BY A.year DESC

SELECT
(SELECT COUNT(1) FROM `tf_demonstration_statistics` WHERE STATUS = 0  AND  TO_DAYS(create_date) = TO_DAYS(NOW())

    AND company_id = #{companyId,jdbcType=BIGINT}


    AND type = #{type,jdbcType=BIGINT}

 ) AS stayDemonstration ,
(SELECT COUNT(1) FROM `tf_demonstration_statistics` WHERE STATUS = 1  AND  TO_DAYS(create_date) = TO_DAYS(NOW())

  AND company_id = #{companyId,jdbcType=BIGINT}


  AND type = #{type,jdbcType=BIGINT}

 ) AS alreadyDemonstration,
IFNULL( ROUND(
( (SELECT COUNT(1) FROM `tf_demonstration_statistics` WHERE TO_DAYS(create_date) = TO_DAYS(NOW())

  AND company_id = #{companyId,jdbcType=BIGINT}


  AND type = #{type,jdbcType=BIGINT}

 )
 -
(SELECT COUNT(1) FROM `tf_demonstration_statistics` WHERE TO_DAYS(NOW()) - TO_DAYS(create_date) = 1

  AND company_id = #{companyId,jdbcType=BIGINT}


  AND type = #{type,jdbcType=BIGINT}

 )
)/(SELECT COUNT(1) FROM `tf_demonstration_statistics` WHERE TO_DAYS(NOW()) - TO_DAYS(create_date) = 1

  AND company_id = #{companyId,jdbcType=BIGINT}


  AND type = #{type,jdbcType=BIGINT}

 ) * 100,2),0) AS increase

SELECT COUNT(1) FROM tf_demonstration_statistics
WHERE

  PERIOD_DIFF( DATE_FORMAT( NOW( ) , '%Y%m' ) , DATE_FORMAT( create_date, '%Y%m' ) ) =#{month,jdbcType=BIGINT}


  AND company_id = #{companyId,jdbcType=BIGINT}


  AND type = #{type,jdbcType=BIGINT}

SELECT COUNT(1) FROM tf_demonstration_statistics
WHERE
1 = 1

  AND YEARWEEK(DATE_FORMAT(create_date,'%Y-%m-%d'),1) = YEARWEEK(NOW(),#{week,jdbcType=BIGINT})


  AND company_id = #{companyId,jdbcType=BIGINT}


  AND type = #{type,jdbcType=BIGINT}

SELECT COUNT(1) FROM tf_demonstration_statistics
WHERE 1 = 1

  AND TO_DAYS(NOW()) - TO_DAYS(create_date) = #{day,jdbcType=BIGINT}


  AND company_id = #{companyId,jdbcType=BIGINT}


  AND type = #{type,jdbcType=BIGINT}

SELECT D.*,DEP.`name` AS departmentName , H.`name` AS hName,A.`full_name` AS afullName ,P.`name` AS pName FROM `tf_demonstration_statistics` D, tf_hospital H, tf_area A, tf_product P , tf_department DEP WHERE D.`hospital` = H.`id` AND D.`area` = A.`id` AND D.`product` = P.`id` AND D.`department` = DEP.`id`
and D.company_id = #{companyId}
and D.type = #{type}
and D.status = #{status}
ORDER BY D.`create_date` DESC

SELECT A.mon, IFNULL(B.countDemonstrationStatistics,0) countDemonstrationStatistics FROM ( SELECT 1 AS mon UNION SELECT 2 UNION SELECT 3 UNION SELECT 4 UNION SELECT 5 UNION SELECT 6 UNION SELECT 7 UNION SELECT 8 UNION SELECT 9 UNION SELECT 10 UNION SELECT 11 UNION SELECT 12 ) A LEFT JOIN ( SELECT COUNT(1) countDemonstrationStatistics ,DATE_FORMAT(create_date,'%m') AS MONTH FROM `tf_demonstration_statistics` WHERE 1 = 1
and company_id = #{companyId}
and product = #{productId}
and type = #{type}
AND DATE_FORMAT(create_date,'%Y') = #{year}
GROUP BY DATE_FORMAT(create_date,'%y-%m') ) B ON A.mon = B.month ORDER BY A.mon
