mapper.select:
select

from tf_knowhow_code
where 1 = 1

  and know_how_id = #{knowHowId,jdbcType=BIGINT}


  and code like '%${code}%'


  and type = #{type,jdbcType=INTEGER}


  and is_sell = true


  and is_sell = false


  and know_how_id = #{knowHowId,jdbcType=BIGINT}

select id, code, face_value, price, DATE_FORMAT(start_date, '%Y-%m-%d %H:%i:%s') start_date,DATE_FORMAT(end_date, '%Y-%m-%d %H:%i:%s') end_date, type, know_how_id, IF(is_sell,'是','否') is_sell from tf_knowhow_code where 1 = 1
and know_how_id = #{knowHowId,jdbcType=BIGINT}
and code like '%${code}%'
and type = #{type,jdbcType=INTEGER}
and is_sell = true
and is_sell = false
and know_how_id = #{knowHowId,jdbcType=BIGINT}
