mapper.insert:
insert into tf_product_statistics (product, member, create_date, 
  ip, iplat, iplng, nation, 
  province, city, district, 
  adcode, hits, company_id
  )
values (#{product,jdbcType=BIGINT}, #{member,jdbcType=BIGINT}, #{createDate,jdbcType=TIMESTAMP}, 
  #{ip,jdbcType=VARCHAR}, #{iplat,jdbcType=VARCHAR}, #{iplng,jdbcType=VARCHAR}, #{nation,jdbcType=VARCHAR}, 
  #{province,jdbcType=VARCHAR}, #{city,jdbcType=VARCHAR}, #{district,jdbcType=VARCHAR}, 
  #{adcode,jdbcType=VARCHAR}, #{hits,jdbcType=BIGINT}, #{companyId,jdbcType=BIGINT}
  )

mapper.select:
SELECT COUNT(1) FROM tf_product_statistics
WHERE

PERIOD_DIFF( DATE_FORMAT( NOW( ) , '%Y%m' ) , DATE_FORMAT( create_date, '%Y%m' ) ) =#{month,jdbcType=BIGINT}


AND company_id = #{companyId,jdbcType=BIGINT}

SELECT COUNT(1) FROM tf_product_statistics WHERE
YEARWEEK(DATE_FORMAT(create_date,'%Y-%m-%d'),1) = YEARWEEK(NOW(),#{week,jdbcType=BIGINT})
AND company_id = #{companyId,jdbcType=BIGINT}

SELECT COUNT(1) FROM tf_product_statistics
WHERE 1 = 1

AND TO_DAYS(NOW()) - TO_DAYS(create_date) = #{day,jdbcType=BIGINT}


AND company_id = #{companyId,jdbcType=BIGINT}

SELECT P.name ,SUM(Ps.hits) AS hist,A.sHist AS sHist ,CONCAT( ROUND((SUM(Ps.hits)/A.sHist)*100,2),'%') AS proportion FROM tf_product P, tf_product_statistics Ps,( SELECT A.`id` id ,A.`name` aname,B.`id` bid, CONCAT(B.`name`,'-',A.`name`) bname FROM tf_product_category A ,tf_product_category B WHERE A.`parent` = B.`id` ) C , (SELECT SUM(hits) AS sHist FROM tf_product_statistics) A WHERE P.`product_category` = C.id AND P.id = Ps.product
AND P.company_id = #{companyId,jdbcType=BIGINT}
GROUP BY NAME

SELECT C.bname ,SUM(Ps.hits) AS hist,A.sHist AS sHist ,CONCAT( ROUND((SUM(Ps.hits)/A.sHist)*100,2),'%') AS proportion FROM tf_product P, tf_product_statistics Ps,( SELECT A.`id` id ,A.`name` aname,B.`id` bid, CONCAT(B.`name`,'>',A.`name`) bname FROM tf_product_category A ,tf_product_category B WHERE A.`parent` = B.`id` ) C , (SELECT SUM(hits) AS sHist FROM tf_product_statistics) A WHERE P.`product_category` = C.id AND P.id = Ps.product
AND P.company_id = #{companyId,jdbcType=BIGINT}
GROUP BY bname

SELECT Ps.province AS cname ,SUM( Ps.hits) AS hits ,A.sHits, CONCAT( ROUND((SUM(Ps.hits)/A.sHits)*100,2),'%') AS proportion FROM tf_product P, tf_product_statistics Ps, (SELECT SUM(hits) AS sHits FROM tf_product_statistics) A WHERE P.id = Ps.product
AND P.company_id = #{companyId,jdbcType=BIGINT}
GROUP BY cname

SELECT DATE_FORMAT(create_date,'%Y-%m') AS TIME,COUNT(1) AS satistics FROM tf_product_statistics WHERE 1 = 1
AND company_id = #{companyId,jdbcType=BIGINT}
GROUP BY TIME ORDER BY TIME DESC LIMIT 12

SELECT * ,CONCAT( ROUND((SUM(A.province_hits)/B.count_hits)*100,2),'%') AS proportion FROM (
SELECT province,SUM(hits) AS province_hits  FROM `tf_product_statistics`   GROUP BY province ORDER BY SUM(hits) DESC
)A, (SELECT SUM(hits) AS count_hits FROM `tf_product_statistics`) B GROUP BY A.province ORDER BY A.province_hits DESC

SELECT B.date ,IFNULL(C.countProduct,0) AS countProduct FROM ( SELECT @a :=@a + 1 AS `index`, DATE(ADDDATE(CURRENT_DATE, INTERVAL @a DAY)) AS `date` FROM mysql.help_topic,(SELECT @a := 0) temp WHERE @a < 6 - WEEKDAY(CURRENT_DATE) UNION SELECT @s :=@s - 1 AS `index`, DATE(DATE_SUB(CURRENT_DATE, INTERVAL @s DAY)) AS `date` FROM mysql.help_topic,(SELECT @s := WEEKDAY(CURRENT_DATE) + 1) temp WHERE @s > 0 ORDER BY `date`) B LEFT JOIN ( SELECT DATE_FORMAT(A.create_date,'%Y-%m-%d') AS 'date', COUNT(A.product) AS countProduct FROM tf_product_statistics A WHERE 1 = 1
AND A.company_id = #{companyId,jdbcType=BIGINT}
GROUP BY DATE_FORMAT(A.create_date,'%Y-%m-%d') ) C ON B.date = C.date ORDER BY B.date

SELECT B.timeDay, IFNULL(C.countProduct,0) AS countProduct FROM ( SELECT DATE_FORMAT(lastWeek.`timeDay`,'%Y-%m-%d') AS'timeDay' FROM ( SELECT DATE_SUB(NOW(),INTERVAL 13 DAY) AS'timeDay' UNION ALL SELECT DATE_SUB(NOW(),INTERVAL 12 DAY) AS'timeDay' UNION ALL SELECT DATE_SUB(NOW(),INTERVAL 11 DAY) AS'timeDay' UNION ALL SELECT DATE_SUB(NOW(),INTERVAL 10 DAY) AS'timeDay' UNION ALL SELECT DATE_SUB(NOW(),INTERVAL 9 DAY) AS'timeDay' UNION ALL SELECT DATE_SUB(NOW(),INTERVAL 8 DAY) AS'timeDay' UNION ALL SELECT DATE_SUB(NOW(),INTERVAL 7 DAY) AS'timeDay' UNION ALL SELECT DATE_SUB(NOW(),INTERVAL 6 DAY) AS'timeDay' UNION ALL SELECT DATE_SUB(NOW(),INTERVAL 5 DAY) AS'timeDay' UNION ALL SELECT DATE_SUB(NOW(),INTERVAL 4 DAY) AS'timeDay' UNION ALL SELECT DATE_SUB(NOW(),INTERVAL 3 DAY) AS'timeDay' UNION ALL SELECT DATE_SUB(NOW(),INTERVAL 2 DAY) AS'timeDay' UNION ALL SELECT DATE_SUB(NOW(),INTERVAL 1 DAY) AS'timeDay' ) AS lastWeek WHERE YEARWEEK(lastWeek.`timeDay`,1) = YEARWEEK(NOW(),1)-1) B LEFT JOIN ( SELECT DATE_FORMAT(A.create_date,'%Y-%m-%d') AS 'date', COUNT(A.product) AS countProduct FROM tf_product_statistics A WHERE 1 = 1
AND company_id = #{companyId,jdbcType=BIGINT}
GROUP BY DATE_FORMAT(A.create_date,'%Y-%m-%d') ) C ON B.timeDay = C.date ORDER BY B.timeDay

SELECT COALESCE(M.name,'-') AS mname , M.username,P.`name`, PS.`ip`,PS.`nation`,PS.`province` FROM tf_product_statistics PS ,
tf_product P, tf_member M
WHERE PS.`product` = P.`id` AND PS.`member` = M.id

  AND PS.`product` = #{productId,jdbcType=BIGINT}


  AND PS.`company_id` = #{companyId,jdbcType=BIGINT}

SELECT A.mon, IFNULL(B.countProductVisits,0) countProductVisits FROM ( SELECT 1 AS mon UNION SELECT 2 UNION SELECT 3 UNION SELECT 4 UNION SELECT 5 UNION SELECT 6 UNION SELECT 7 UNION SELECT 8 UNION SELECT 9 UNION SELECT 10 UNION SELECT 11 UNION SELECT 12 ) A LEFT JOIN ( SELECT COUNT(1) countProductVisits ,DATE_FORMAT(create_date,'%m') AS MONTH FROM `tf_product_statistics` WHERE 1 = 1
AND product = #{productId,jdbcType=BIGINT}
AND company_id = #{companyId,jdbcType=BIGINT}
AND DATE_FORMAT(create_date,'%Y') = #{year}
GROUP BY DATE_FORMAT(create_date,'%y-%m') ) B ON A.mon = B.month ORDER BY A.mon

SELECT P.`name` ,COUNT(1) AS countProductVisits FROM tf_product P LEFT JOIN tf_product_statistics PS ON P.`id` = PS.`product`
WHERE 1 = 1

  AND P.company_id = #{companyId,jdbcType=BIGINT}


  AND PS.create_date BETWEEN #{startDate,jdbcType=TIMESTAMP} AND #{endDate,jdbcType=TIMESTAMP}

GROUP BY P.`name`

SELECT * FROM (
SELECT COUNT(1) AS countProduct FROM tf_product_statistics WHERE 1 = 1

    AND company_id = #{companyId,jdbcType=BIGINT}


    AND product = #{productId,jdbcType=BIGINT}


    AND create_date BETWEEN #{startDate,jdbcType=TIMESTAMP} AND #{endDate,jdbcType=TIMESTAMP}

) A ,(
SELECT COUNT(1) AS countProductDemonstration FROM `tf_demonstration_statistics` WHERE TYPE = 0

    AND company_id = #{companyId,jdbcType=BIGINT}


    AND product = #{productId,jdbcType=BIGINT}


    AND create_date BETWEEN #{startDate,jdbcType=TIMESTAMP} AND #{endDate,jdbcType=TIMESTAMP}

) B

SELECT * FROM (
SELECT COUNT(1) AS countProductDemonstration0 FROM `tf_demonstration_statistics` WHERE TYPE = 0

    AND company_id = #{companyId,jdbcType=BIGINT}


    AND product = #{productId,jdbcType=BIGINT}


    AND create_date BETWEEN #{startDate,jdbcType=TIMESTAMP} AND #{endDate,jdbcType=TIMESTAMP}

) A ,(
SELECT COUNT(1) AS countProductDemonstration1 FROM `tf_demonstration_statistics` WHERE TYPE = 1

    AND company_id = #{companyId,jdbcType=BIGINT}


    AND product = #{productId,jdbcType=BIGINT}


    AND create_date BETWEEN #{startDate,jdbcType=TIMESTAMP} AND #{endDate,jdbcType=TIMESTAMP}

) B

SELECT * FROM (
SELECT COUNT(1) AS countProductDemonstration0 FROM `tf_demonstration_statistics` WHERE TYPE = 0

    AND company_id = #{companyId,jdbcType=BIGINT}


    AND create_date BETWEEN #{startDate,jdbcType=TIMESTAMP} AND #{endDate,jdbcType=TIMESTAMP}

) A ,(
SELECT COUNT(1) AS countOrder FROM `tf_order`  WHERE 1 = 1

    AND company_id = #{companyId,jdbcType=BIGINT}


    AND create_date BETWEEN #{startDate,jdbcType=TIMESTAMP} AND #{endDate,jdbcType=TIMESTAMP}

) B

SELECT * FROM (
SELECT COUNT(1) AS countProduct FROM tf_product_statistics WHERE 1 = 1

    AND company_id = #{companyId,jdbcType=BIGINT}


    AND create_date BETWEEN #{startDate,jdbcType=TIMESTAMP} AND #{endDate,jdbcType=TIMESTAMP}

) A ,(
SELECT COUNT(1) AS countCompany FROM tf_company_statistics WHERE 1 = 1

    AND company_id = #{companyId,jdbcType=BIGINT}


    AND create_date BETWEEN #{startDate,jdbcType=TIMESTAMP} AND #{endDate,jdbcType=TIMESTAMP}

) B
