mapper.select:
SELECT RP.*,C.name cName ,PC.bname pcName FROM tf_recommend_product RP
LEFT JOIN tf_company C ON RP.company_id = C.id
LEFT JOIN (
SELECT A.`id` id ,A.`name` aname,B.`id` bid, CONCAT(B.`name`,'-',A.`name`) bname FROM tf_product_category A ,tf_product_category B WHERE A.`parent` = B.`id`
) PC ON RP.category_id = PC.id
WHERE 1 = 1

  AND RP.company_id = #{companyId,jdbcType=BIGINT}


  AND RP.title LIKE '%${title}%'


  and RP.create_date BETWEEN #{startDate,jdbcType=TIMESTAMP}  AND #{endDate,jdbcType=TIMESTAMP}


  AND RP.status = #{status,jdbcType=BIGINT}

SELECT rp.*
FROM tf_recommend_product rp
WHERE rp.status  = 2
and rp.payment_status = 2
AND NOW() >= rp.begin_date
AND NOW() <= rp.end_date

  AND rp.category_id = #{categoryId}


AND rp.company_id = #{companyId,jdbcType=BIGINT}


AND rp.type = #{type,jdbcType=BIGINT}
